import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button';
import './Home.css';
import HelpIcon from '@mui/icons-material/Help';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';

function AddBudgets() {
    const [name, setName] = useState('');
    const [budgetLimit, setBudgetLimit] = useState('');
    const [type, setType] = useState('');
    const [tag, setTag] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('/api/budgets', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    name,
                    type,
                    budgetLimit: parseFloat(budgetLimit),
                    tag
                }),
            });

            const data = await response.json();

            if (!response.ok) {
                alert(data.error);
                return;
            }

            console.log(data);
            navigate('/viewBudgets');
        } catch (error) {
            console.error('Error adding budget:', error);
            alert('Failed to add budget. Please try again.');
        }
    };

    return (
        <div className="AddBudget">
            <header className="header">
                <Stack direction="row" spacing={65} sx={{
                    justifyContent: "center",
                    alignItems: "center",
                }}>
                    <Button>
                        <HelpIcon style={{ color: 'black' }} />
                    </Button>
                    <h1>WalletWatch</h1>
                    <Link to="/dashboard">
                        <Button style={{ color: 'white', backgroundColor: 'black', height: 30 }}>
                            <p>Home</p>
                        </Button>
                    </Link>
                </Stack>
            </header>
            <h2>Add a Budget</h2>
            <form onSubmit={handleSubmit}>
                <Stack spacing={2} sx={{
                    justifyContent: "center",
                    alignItems: "center"
                }}>
                    <TextField
                        required
                        label="Budget Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <FormControl required sx={{ minWidth: 213.17 }}>
                        <InputLabel>Budget Type</InputLabel>
                        <Select
                            value={type}
                            label="Budget Type"
                            onChange={(e) => setType(e.target.value)}
                        >
                            <MenuItem value="spending">Spending</MenuItem>
                            <MenuItem value="savings">Savings</MenuItem>
                        </Select>
                    </FormControl>
                    <TextField
                        required
                        label="Budget Amount"
                        type="number"
                        value={budgetLimit}
                        onChange={(e) => setBudgetLimit(e.target.value)}
                    />
                    <FormControl required sx={{ minWidth: 213.17 }}>
                        <InputLabel>Category</InputLabel>
                        <Select
                            value={tag}
                            label="Category"
                            onChange={(e) => setTag(e.target.value)}
                        >
                            <MenuItem value="housing">Housing</MenuItem>
                            <MenuItem value="utilities">Utilities</MenuItem>
                            <MenuItem value="groceries">Groceries</MenuItem>
                            <MenuItem value="transportation">Transportation</MenuItem>
                            <MenuItem value="healthcare">Healthcare</MenuItem>
                            <MenuItem value="dining">Dining Out</MenuItem>
                            <MenuItem value="entertainment">Entertainment</MenuItem>
                            <MenuItem value="shopping">Shopping</MenuItem>
                            <MenuItem value="travel">Travel</MenuItem>
                            <MenuItem value="fitness">Fitness</MenuItem>
                            <MenuItem value="education">Education</MenuItem>
                            <MenuItem value="professional">Professional Development</MenuItem>
                            <MenuItem value="technology">Technology</MenuItem>
                            <MenuItem value="savings">Savings</MenuItem>
                            <MenuItem value="investments">Investments</MenuItem>
                            <MenuItem value="emergency">Emergency Fund</MenuItem>
                            <MenuItem value="retirement">Retirement</MenuItem>
                            <MenuItem value="gifts">Gifts</MenuItem>
                            <MenuItem value="hobbies">Hobbies</MenuItem>
                            <MenuItem value="petcare">Pet Care</MenuItem>
                            <MenuItem value="personalcare">Personal Care</MenuItem>
                            <MenuItem value="insurance">Insurance</MenuItem>
                            <MenuItem value="subscriptions">Subscriptions</MenuItem>
                        </Select>
                    </FormControl>
                    <Button
                        type="submit"
                        style={{
                            float: 'right',
                            color: 'white',
                            backgroundColor: 'black',
                            height: 30,
                            width: 200
                        }}
                    >
                        Submit
                    </Button>
                </Stack>
                <br />
            </form>
            <footer className="footer">
                {/* Your existing footer code */}
            </footer>
        </div>
    );
}

export default AddBudgets;

