import { useNavigate } from 'react-router-dom';
import React, { useState, useEffect } from 'react'; // Only import React once
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import Button from '@mui/material/Button';
import HelpIcon from '@mui/icons-material/Help';
import Stack from '@mui/material/Stack';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import Paper from "@mui/material/Paper";
import Box from '@mui/material/Box';

const Transactions = () => {
    const navigate = useNavigate();
    const [transactions, setTransactions] = useState([]); // Define transactions state

    const EditTransactionPage = () => {
        const { id } = useParams(); // Access the transaction ID
        // Fetch and display the transaction for editing using the ID
    };

    useEffect(() => {
        // Fetch transactions from an API or other data source
        fetch('/api/transactions')
          .then((response) => response.json())
          .then((data) => setTransactions(data));
      }, []);

    // Function to fetch transactions
    const fetchTransactions = async () => {
        try {
            const response = await fetch('/api/transactions');
            if (!response.ok) {
                throw new Error('Failed to fetch transactions');
            }
            const data = await response.json();
            setTransactions(data);
        } catch (error) {
            console.error('Error fetching transactions:', error);
        }
    };

    return (
        <div className="ViewTransactions">
            <header className="header">
                <Stack direction="row" spacing={65} sx={{
                    justifyContent: "center",
                    alignItems: "center",
                }}>
                    <Button>
                        <HelpIcon style={{ color: 'black' }}></HelpIcon>
                    </Button>

                    <h1> WalletWatch</h1>
                    <Link to="/dashboard">
                        <Button style={{ color: 'white', backgroundColor: 'black', height: 30 }}>
                            <p>Home</p>
                        </Button>
                    </Link>
                </Stack>
            </header>
            <h2> Your Transactions</h2>
            <Box sx={{ width: '100%', height: '50vh', borderColor: 'divider' }}>
                <Paper square elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                    <List
                        sx={{
                            maxHeight: 'calc(100% - 48px)',
                            overflow: 'auto',
                            '&::-webkit-scrollbar': {
                                width: '0.4em',
                            },
                            '&::-webkit-scrollbar-track': {
                                background: '#f1f1f1',
                            },
                            '&::-webkit-scrollbar-thumb': {
                                backgroundColor: '#888',
                            },
                        }}
                    >
                        {transactions && transactions.length > 0 ? (
                            transactions.map((transaction) => (
                                <React.Fragment key={transaction.id}>
                                    <ListItem
                                        sx={{
                                            backgroundColor: '#f0f0f0',
                                            marginBottom: '8px',
                                            padding: '16px',
                                        }}
                                    >
                                        <Stack direction="column" sx={{ flexGrow: 1 }}>
                                            <ListItemText
                                                primary={transaction.transactionName}
                                                secondary={
                                                    <React.Fragment>
                                                        <div>{new Date(transaction.date).toLocaleDateString()}</div>
                                                        <div>${transaction.number ? transaction.number.toFixed(2) : '0.00'}</div> {/* Handle undefined 'number' */}
                                                        <div style={{ color: 'gray' }}>Tag: {transaction.tag}</div>
                                                        <div style={{ color: 'gray' }}>Category: {transaction.category}</div>
                                                        <div style={{ color: 'black', fontWeight: 'bold' }}>
                                                            Type: {transaction.type === 'savings' ? 'Saving' : 'Spending'}
                                                        </div> {/* Displaying the type (Saving/Spending) */}
                                                    </React.Fragment>
                                                }
                                                sx={{
                                                    '& .MuiListItemText-primary': {
                                                        fontWeight: 'bold',
                                                    },
                                                }}
                                            />
                                        </Stack>
                                        <Link to="/edittransactions">
                                            <Button
                                                style={{
                                                    color: 'white',
                                                    backgroundColor: 'black',
                                                    height: 30,
                                                    borderRadius: '4px',
                                                }}
                                            >
                                                Edit
                                            </Button>
                                        </Link>
                                    </ListItem>
                                </React.Fragment>
                            ))
                        ) : (
                            <ListItem>
                                <ListItemText primary="No transactions found" />
                            </ListItem>
                        )}
                    </List>
                </Paper>
            </Box>
            <div style={{ marginTop: '20px', display: 'flex', gap: '20px' }}>
                <Link to="/addtransactions">
                    <Button style={{ color: 'white', backgroundColor: 'black', height: 30, width: 200 }}>
                        <p>Add</p>
                    </Button>
                </Link>

                <Link to="/deletetransactions">
                    <Button style={{ color: 'white', backgroundColor: 'black', height: 30, width: 200 }}>
                        <p>Delete</p>
                    </Button>
                </Link>
            </div>
            <footer className="footer">
                <Stack direction="row" spacing={50} sx={{
                    justifyContent: "center",
                    alignItems: "center",
                }}>
                    <Link to="/transactions">
                        <Button style={{ color: 'white', backgroundColor: 'black', height: 30, width: 200 }}>
                            <p>View Transactions</p>
                        </Button>
                    </Link>
                    <Link to="/viewBudgets">
                        <Button style={{ color: 'white', backgroundColor: 'black', height: 30, width: 200 }}>
                            <p>View Budgets</p>
                        </Button>
                    </Link>
                    <Link to="/createAccount">
                        <Button style={{ color: 'white', backgroundColor: 'black', height: 30, width: 200 }}>
                            <p>Profile</p>
                        </Button>
                    </Link>
                </Stack>
            </footer>
        </div>
    );
};

export default Transactions;
